def get_vector(d):
  return '''
    <path
      android:fillColor="#FF607D8B"
      android:pathData="{}"/>
  '''.format(d)

def get_vector_dark(d):
  return '''
    <path
      android:fillColor="#FAFAFAFA"
      android:pathData="{}"/>
  '''.format(d)  

def get_svg(d):
  return '''
  <path fill="#546E7A" d="{}" />
  '''.format(d)

def get_svg_dark(d):
  return '''
  <path fill="#FAFAFA" d="{}" />
  '''.format(d)  

def convert_svg_to_png(dest):
  cmd = 'convert -density 1200 -resize 128x128 temp.svg -transparent white {}'.format(dest)
  os.system(cmd)


cur_dir = os.path.dirname(os.path.realpath(__file__))
vector_path = os.path.join(cur_dir, '..', 'android', 'app', 'src', 'main', 'res', 'drawable')
vector_template = open(os.path.join(cur_dir, 'category_vector_template.xml')).read()
svg_template = open(os.path.join(cur_dir, 'category_svg_template.svg')).read()

for key in categories_path_data:
  print('Generating {}...'.format(key))
  dest = os.path.join(vector_path, 'category_{}.xml'.format(key.lower()))
  val = categories_path_data[key]
  if type(val) is str:
    val = [val]
  with open(dest, 'w+') as f:
    f.write(vector_template.replace('<!--FILLCOLOR-->', '#fff').replace('<!--DATA-->', ''.join(list(map(get_vector, val)))))

  dest = os.path.join(vector_path, 'category_{}_dark.xml'.format(key.lower()))
  val = categories_path_data[key]
  if type(val) is str:
    val = [val]
  with open(dest, 'w+') as f:
    f.write(vector_template.replace('<!--FILLCOLOR-->', '#424242').replace('<!--DATA-->', ''.join(list(map(get_vector_dark, val)))))    

  tmp_path = 'temp.svg'
  dest = os.path.join(cur_dir, '..', 'assets', 'category_{}.png'.format(key.lower()))
  with open(tmp_path, 'w+') as f:
    f.write(svg_template.replace('<!--DATA-->', ''.join(list(map(get_svg, val)))))
  convert_svg_to_png(dest)

  dest = os.path.join(cur_dir, '..', 'assets', 'category_{}_dark.png'.format(key.lower()))
  with open(tmp_path, 'w+') as f:
    f.write(svg_template.replace('<!--DATA-->', ''.join(list(map(get_svg_dark, val)))))
  convert_svg_to_png(dest)  

print('DONE')